// page init
jQuery(function(){
	initTabs();
	initCarousel();
	initSlideShow();
	initMobileNav();
});

// scroll gallery init
function initCarousel() {
	jQuery('.carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		generatePagination: '.pagination',
		autoRotation: true,
		stretchSlideToMask: true,
		maskAutoSize: true,
		switchTime: 1000,
		animSpeed: 500,
		step: 1
	});
	jQuery('.quote').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		vertical: true,
		autoRotation: false,
		switchTime: 1000,
		animSpeed: 500
	});
	jQuery('.pp-carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		generatePagination: '.pagination',
		autoRotation: true,
		switchTime: 1000,
		animSpeed: 500
	});
        jQuery('.tab-carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		stretchSlideToMask: true,
		maskAutoSize: true,
//		generatePagination: '.pagination_new',
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
	jQuery('#tab-carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination_new li',
		stretchSlideToMask: true,
		maskAutoSize: true,
		generatePagination: '.pagination_new',
		autoRotation: true,
		switchTime: 5000,
		animSpeed: 500
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('div.slideshow').fadeGallery({
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination_new li',
                generatePagination: '.pagination_new',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
}

// content tabs init
function initTabs() {
	jQuery('.p-holder').contentTabs({
		autoHeight: true,
		checkHash: true,
		animSpeed: 400,
		effect: 'slide',
		tabLinks: 'a'
	});
	jQuery('.main-p-holder').contentTabs({
		autoHeight: true,
		checkHash: true,
		animSpeed: 400,
		effect: 'slide',
		tabLinks: 'a'
	});
	jQuery('.indoor-tab').contentTabs({
		autoHeight: true,
		checkHash: true,
		animSpeed: 400,
		effect: 'slide',
		tabLinks: 'a'
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('#header').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '.nav-drop'
	});
	jQuery('#header').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active1',
		menuOpener: '.nav-opener1',
		menuDrop: '#nav ul'
	});
	jQuery('.lamp-holder').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'active',
		menuOpener: '.lamp',
		menuDrop: '.lamp-holder ul'
	});
}

